var Hangman=function(c,b){this.element=b;this.wordArray=c.split("");this.currentWord=[];var a=this.wordArray.length;while(a){this.currentWord.push("?");a--;}this.tryCount=0;this.maxTries=3;this.init();};Hangman.prototype.init=function(){this.gameForm=document.createElement("form");var a=document.createElement("input");a.name="letter";var d=document.createElement("input");d.type="submit";var b=document.createElement("label");b.text="letter:";b.appendChild(a);this.gameForm.appendChild(b);this.gameForm.appendChild(d);var c=this;this.gameForm.onsubmit=function(f){f.preventDefault();c.tryLetter(this.letter.value);this.letter.value="";};this.display=document.createElement("p");this.display.style.fontSize="3em";this.display.innerHTML=this.currentWord.join("");this.status=document.createElement("p");this.element.appendChild(this.gameForm);this.element.appendChild(this.display);this.element.appendChild(this.status);};Hangman.prototype.setMaxTries=function(a){this.maxTries=a;};Hangman.prototype.setStatus=function(a){this.tryCount=a;};Hangman.prototype.getStatus=function(){return this.tryCount;};Hangman.prototype.getWordArray=function(){return this.wordArray;};Hangman.prototype.getCurrentWord=function(){return this.currentWord;};Hangman.prototype.getLetterInWord=function(b){var c=0;for(var a=0;a<this.wordArray.length;a++){if(this.wordArray[a]===b){c++;}}return c;};Hangman.prototype.getLetterPositionsInWord=function(b){var c=[];for(var a=0;a<this.wordArray.length;a++){if(this.wordArray[a]===b){c.push(a);}}return c;};Hangman.prototype.tryLetter=function(b){var c=1;for(var a=0;a<this.wordArray.length;a++){if(this.wordArray[a]===b){this.currentWord[a]=b;c=0;}}if(c){this.tryCount++;if(this.tryCount===this.maxTries){this.setStatus("game over");this.gameForm.letter.disabled=true;}}if(this.currentWord.indexOf("?")===-1){this.setStatus("you win");}this.display.innerHTML=this.currentWord.join("");this.status.innerHTML=this.tryCount;};Hangman.prototype.getTryCount=function(){return this.tryCount;};